body.script:
//
const fetchWithCacheManager = (() => {
  const CACHE_DURATION_MS = 10 * 60 * 1000; // 10 minutos por defecto

  // Funci√≥n para obtener datos con cach√©
  async function fetchWithCache(url, cacheDuration = CACHE_DURATION_MS) {
    try {
      const cacheKey = `cache_feed_${url}`;
      const cached = localStorage.getItem(cacheKey);

      if (cached) {
        const cachedObj = JSON.parse(cached);
        const age = Date.now() - cachedObj.timestamp;
        if (age < cacheDuration) {
          // Cache v√°lida
          return cachedObj.data;
        } else {
          // Cache vencida, eliminar
          localStorage.removeItem(cacheKey);
        }
      }

      // Hacer fetch real
      const response = await fetch(url);
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

      const data = await response.json();

      // Guardar en cache con timestamp
      localStorage.setItem(cacheKey, JSON.stringify({
        timestamp: Date.now(),
        data: data
      }));

      return data;

    } catch (error) {
      console.error(`fetchWithCache error para URL: ${url}`, error);

      // Intentar devolver cache aunque est√© vencida para no perder funcionalidad
      try {
        const cached = localStorage.getItem(`cache_feed_${url}`);
        if (cached) {
          const cachedObj = JSON.parse(cached);
          return cachedObj.data;
        }
      } catch { /* ignorar */ }

      throw error; // No hay cache y fetch fall√≥
    }
  }

  return fetchWithCache;
})();
