document.addEventListener("DOMContentLoaded", function () {
  const tocContainer = document.getElementById("toc-container");
  const toc = document.getElementById("toc");
  const toggle = document.getElementById("toc-toggle");

  const content = document.querySelector(".post-body"); // Aseg√∫rate que es el contenedor del post

  if (!content || !toc) return;

  // Forzar estado inicial oculto (importante para m√≥viles)
  toc.setAttribute("hidden", "true");
  toggle.setAttribute("aria-expanded", "false");
  toggle.textContent = "üìë Mostrar tabla de contenido";

  const headers = content.querySelectorAll("h2, h3");

  if (headers.length === 0) {
    tocContainer.style.display = "none";
    return;
  }

  // Agrega encabezado sem√°ntico dentro del <nav>
  const title = document.createElement("strong");
  title.textContent = "Tabla de contenido";
  toc.appendChild(title);

  const ul = document.createElement("ul");
  let currentUl = ul;
  let lastLevel = 2;

  headers.forEach((header, index) => {
    // Agrega id si no lo tiene (necesario para enlaces internos)
    if (!header.id) header.id = "toc-header-" + index;
    const level = parseInt(header.tagName.replace("H", ""), 10);

    const li = document.createElement("li");
    const a = document.createElement("a");
    a.href = "#" + header.id;
    a.textContent = header.textContent;
    li.appendChild(a);

    if (level === lastLevel) {
      currentUl.appendChild(li);
    } else if (level > lastLevel) {
      const newUl = document.createElement("ul");
      newUl.appendChild(li);
      const lastLi = currentUl.lastElementChild;
      if (lastLi) lastLi.appendChild(newUl);
      currentUl = newUl;
    } else {
      let parent = currentUl.parentElement;
      while (parent && parent.tagName === "UL" && level < lastLevel) {
        currentUl = parent;
        parent = currentUl.parentElement;
        lastLevel--;
      }
      currentUl.appendChild(li);
    }

    lastLevel = level;
  });

  toc.appendChild(ul);

  // Mostrar u ocultar TOC al hacer clic en el bot√≥n
  toggle.addEventListener("click", () => {
    const isHidden = toc.hasAttribute("hidden");
    if (isHidden) {
      toc.removeAttribute("hidden");
      toggle.textContent = "üìë Ocultar tabla de contenido";
      toggle.setAttribute("aria-expanded", "true");
    } else {
      toc.setAttribute("hidden", "true");
      toggle.textContent = "üìë Mostrar tabla de contenido";
      toggle.setAttribute("aria-expanded", "false");
    }
  });
});
