document.addEventListener("DOMContentLoaded", function() {
  // Evitar ejecuci√≥n en p√°ginas est√°ticas
  if (window.location.pathname.includes('/p/')) return;
  const postBody = document.querySelector('.post-body');
  if (!postBody) return;

  const paragraphs = postBody.querySelectorAll('p');
  if (paragraphs.length > 2) {
    const adDiv = document.createElement('div');
    adDiv.className = 'adsense-shortcode'; // mejor que adsense-ad

    const monetized = false; // cambiar a false si quieres ver placeholders

    if (monetized) {
      adDiv.innerHTML = `
        <ins class="adsbygoogle"
             style="display:block"
             data-ad-client="ca-pub-XXXXXXXXXX"
             data-ad-slot="2DO_PARRAFO_SLOT_1"
             data-ad-format="auto"
             data-full-width-responsive="true"></ins>
      `;
      // Forzar ejecuci√≥n segura
      if (window.adsbygoogle) {
        try {
          (adsbygoogle = window.adsbygoogle || []).push({});
        } catch(e) { console.log("Error adsbygoogle:", e); }
      }
    } else {
      adDiv.innerHTML = `<div class="adsense-placeholder">[Espacio reservado para anuncio post 3]</div>`;
    }

    paragraphs[1].parentNode.insertBefore(adDiv, paragraphs[1].nextSibling);
  }
});
