document.addEventListener('DOMContentLoaded', function() {
  const monetized = false; // Por defecto true o Cambiar a false para mostrar mensajes en vez de anuncios

  // Mapa con c√≥digos AdSense o mensajes seg√∫n el data-code
  const adsenseCodes = {
    'adsense-index-1': monetized ? `
      <ins class="adsbygoogle"
           style="display:block"
           data-ad-client="ca-pub-XXXXXXXXXX"
           data-ad-slot="INDEX_SLOT_1"
           data-ad-format="auto"
           data-full-width-responsive="true"></ins>
    ` : `<div class="adsense-placeholder">[Espacio reservado para anuncio index 1]</div>`,

    'adsense-index-2': monetized ? `
      <ins class="adsbygoogle"
           style="display:block"
           data-ad-client="ca-pub-XXXXXXXXXX"
           data-ad-slot="INDEX_SLOT_1"
           data-ad-format="auto"
           data-full-width-responsive="true"></ins>
    ` : `<div class="adsense-placeholder">[Espacio reservado para anuncio index 2]</div>`,

    'adsense-post-1': monetized ? `
      <ins class="adsbygoogle"
           style="display:block"
           data-ad-client="ca-pub-XXXXXXXXXX"
           data-ad-slot="POST_SLOT_1"
           data-ad-format="auto"
