document.addEventListener("DOMContentLoaded", () => {
  const menuBtn  = document.querySelector(".menu-toggle");      // Bot√≥n orbital
  const menuList = document.querySelector("nav.main-nav ul");   // Men√∫ orbital
  const footer   = document.querySelector("footer");            // Footer
  const cartBtn  = document.querySelector(".cart-float");       // Bot√≥n contador

  const isMobile = () => window.innerWidth <= 768;

  // --- Apertura / cierre men√∫ orbital ---
  if (menuBtn && menuList) {
    menuBtn.addEventListener("click", () => {
      menuList.classList.toggle("active");
      menuBtn.classList.toggle("open"); // Cambia a "X"
    });
  }

  // --- Ajuste del carrito flotante (m√≥vil y desktop) ---
  function ajustarCarrito() {
    if (!cartBtn || !footer) return;

    const vh = window.innerHeight;
    const footerTop = footer.getBoundingClientRect().top;

    // Si el footer entra en pantalla, elevamos el bot√≥n; m√≠nimo 20px desde abajo
    const overlap = footerTop < vh ? (vh - footerTop + 20) : 20;
    cartBtn.style.bottom = overlap + "px";
  }

  // --- Ajuste del orbital respecto al carrito y footer (solo m√≥vil) ---
  function ajustarOrbital() {
    if (!menuBtn || !footer) return;
    if (!isMobile()) {
      menuBtn.style.bottom = "16px"; // desktop orbital deshabilitado
      return;
    }

    const carritoVisible = window.TIENDA_CONFIG?.mostrarContador ?? false;

    // Posici√≥n inicial orbital
    let baseBottom = carritoVisible ? 80 : 16;   

    // Margen al llegar al footer
    let margenFooter = carritoVisible ? 80 : 20; 

    const footerTop = footer.getBoundingClientRect().top;
    const vh = window.innerHeight;

    // Si el footer entra en pantalla, el orbital sube
    const overlap = footerTop < vh ? (vh - footerTop + margenFooter) : 0;

    // Posici√≥n final
    const btnBottom = overlap > 0 ? overlap : baseBottom;
    menuBtn.style.bottom = btnBottom + "px";

    // Men√∫ orbital siempre 60px por encima del orbital
    if (menuList) {
      menuList.style.bottom = (btnBottom + 60) + "px";
    }
  }

  // --- Inicializar posiciones ---
  function ajustarTodo() {
    ajustarCarrito();
    ajustarOrbital();
  }

  // Ejecutar al inicio y en scroll/resize
  setTimeout(ajustarTodo, 50); // Primer render
  window.addEventListener("scroll", ajustarTodo, { passive: true });
  window.addEventListener("resize", ajustarTodo);
});
