document.addEventListener("DOMContentLoaded", function () {
  const boxes = document.querySelectorAll(".download-box");

  boxes.forEach(box => {
    let url = box.getAttribute("data-url");
    let time = parseInt(box.getAttribute("data-time"), 10);

    // Funci√≥n para mostrar formato reloj mm:ss
    function formatTime(seconds) {
      let m = String(Math.floor(seconds / 60)).padStart(2, "0");
      let s = String(seconds % 60).padStart(2, "0");
      return `${m}:${s}`;
    }

    // Aplicar estilos inline
    box.style.textAlign = "center";
    box.style.fontFamily = "system-ui, sans-serif";
    box.style.background = "#f9f9f9";
    box.style.padding = "15px";
    box.style.margin = "20px auto";
    box.style.borderRadius = "8px";
    box.style.border = "2px dashed #ccc";
    box.style.maxWidth = "400px";

    // Crear texto inicial
    box.innerHTML = `
      <p style="margin:0; font-size:16px;">
        Tu descarga empezar√° en <span class="counter">${formatTime(time)}</span> segundos...
      </p>
    `;

    let counter = box.querySelector(".counter");
    let interval = setInterval(() => {
      time--;
      counter.textContent = formatTime(time);
      if (time <= 0) {
        clearInterval(interval);
        window.open(url, "_blank");
        box.innerHTML = `
          <p style="margin:0; font-size:16px;">
            Si tu descarga no empieza <a href="${url}" target="_blank" rel="noopener">da clic aqu√≠</a>.
          </p>
        `;
      }
    }, 1000);
  });
});
