const searchInput = document.getElementById("ajax-search-input");
const searchForm = document.getElementById("ajax-search-form");
const resultsContainer = document.getElementById("search-results");
let searchTimeout;
const MAX_RESULTS = 10;

searchInput.addEventListener("input", function () {
  clearTimeout(searchTimeout);
  let query = this.value.trim();

  if (query.length < 3) {
    resultsContainer.innerHTML = "<p>Escribe al menos 3 caracteres...</p>";
    return;
  }

  searchTimeout = setTimeout(() => {
    resultsContainer.innerHTML = "<p>Buscando...</p>";
    let url = `/feeds/posts/summary?alt=json&q=${encodeURIComponent(query)}&max-results=500`;

    fetchWithCacheManager(url, 600000) // 10 min cach√©
      .then(data => {
        let entries = data.feed.entry || [];
        if (entries.length === 0) {
          resultsContainer.innerHTML = "<p>No se encontraron resultados.</p>";
          return;
        }

        // Tomamos solo los primeros MAX_RESULTS
        const mostrar = entries.slice(0, MAX_RESULTS);
        let html = "<ul class='search-result-list'>";
        mostrar.forEach(entry => {
          let title = entry.title.$t;
          let link = entry.link.find(l => l.rel === "alternate").href;
          let snippet = entry.summary ? entry.summary.$t.substring(0, 120) + "..." : "";
          html += `
            <li class="search-result-item">
              <a href="${link}" target="_blank">${title}</a>
              <p>${snippet}</p>
            </li>`;
        });

        // Si hay m√°s resultados que MAX_RESULTS, a√±adimos enlace
        if (entries.length > MAX_RESULTS) {
          html += `<li class="search-result-item more-results">
                     <a href="/search?q=${encodeURIComponent(query)}">
                       Ver todos los resultados ‚Üí
                     </a>
                   </li>`;
        }

        html += "</ul>";
        resultsContainer.innerHTML = html;
      })
      .catch(err => {
        resultsContainer.innerHTML = "<p>Error al buscar. Intenta de nuevo.</p>";
        console.error(err);
      });

  }, 500); // medio segundo de espera tras dejar de escribir
});

// --- Fallback SEO (submit cl√°sico) ---
searchForm.addEventListener("submit", function (e) {
  e.preventDefault();
  let query = searchInput.value.trim();
  if (query.length >= 3) {
    searchInput.dispatchEvent(new Event("input")); // reutilizamos live search
  }
});
